import React, { useState, useEffect, useRef } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';
import axios from 'axios';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const PriceChart = ({ prices, ws }) => {
  const [chartData, setChartData] = useState({
    datasets: []
  });
  const [timeRange, setTimeRange] = useState(24);
  const [realtimeData, setRealtimeData] = useState({});
  const chartRef = useRef();

  const exchangeColors = {
    'bitFlyer': '#ff6b6b',
    'Coincheck': '#4ecdc4', 
    'Zaif': '#45b7d1',
    'GMO„Ç≥„Ç§„É≥': '#96ceb4',
    'bitbank': '#ffeaa7'
  };

  const fetchPriceHistory = async () => {
    try {
      const response = await axios.get(`/api/price-history?hours=${timeRange}`);
      const priceHistory = response.data.priceHistory;

      // „Éá„Éº„Çø„ÇíÂèñÂºïÊâÄ„Åî„Å®„Å´„Ç∞„É´„Éº„ÉóÂåñ
      const groupedData = {};
      priceHistory.forEach(record => {
        if (!groupedData[record.exchange]) {
          groupedData[record.exchange] = [];
        }
        groupedData[record.exchange].push({
          x: new Date(record.timestamp),
          y: record.price
        });
      });

      // Chart.jsÁî®„ÅÆ„Éá„Éº„Çø„Çª„ÉÉ„Éà„Çí‰ΩúÊàê
      const datasets = Object.keys(groupedData).map(exchange => ({
        label: exchange,
        data: groupedData[exchange],
        borderColor: exchangeColors[exchange] || '#666',
        backgroundColor: exchangeColors[exchange] || '#666',
        borderWidth: 2,
        fill: false,
        tension: 0.1,
        pointRadius: 1,
        pointHoverRadius: 4
      }));

      setChartData({ datasets });
    } catch (error) {
      console.error('Error fetching price history:', error);
    }
  };

  // „É™„Ç¢„É´„Çø„Ç§„É†„Éá„Éº„Çø„Åß„ÉÅ„É£„Éº„Éà„ÇíÊõ¥Êñ∞
  const updateChartWithRealtime = (newPrices) => {
    if (!newPrices || newPrices.length === 0) return;

    const now = new Date();
    const newRealtimeData = { ...realtimeData };

    newPrices.forEach(price => {
      if (!newRealtimeData[price.exchange]) {
        newRealtimeData[price.exchange] = [];
      }

      // ÊúÄÊñ∞„ÅÆ„Éá„Éº„Çø„Éù„Ç§„É≥„Éà„ÇíËøΩÂä†
      newRealtimeData[price.exchange].push({
        x: now,
        y: price.price
      });

      // Âè§„ÅÑ„Éá„Éº„Çø„ÇíÂâäÈô§ÔºàÁõ¥Ëøë1ÊôÇÈñìÂàÜ„ÅÆ„Åø‰øùÊåÅÔºâ
      const oneHourAgo = new Date(now.getTime() - 60 * 60 * 1000);
      newRealtimeData[price.exchange] = newRealtimeData[price.exchange]
        .filter(point => point.x > oneHourAgo);
    });

    setRealtimeData(newRealtimeData);

    // ÁèæÂú®„ÅÆ„ÉÅ„É£„Éº„Éà„Éá„Éº„Çø„Å´„É™„Ç¢„É´„Çø„Ç§„É†„Éá„Éº„Çø„ÇíËøΩÂä†
    setChartData(prevData => {
      const updatedDatasets = prevData.datasets.map(dataset => {
        const exchangeName = dataset.label;
        const realtimePoints = newRealtimeData[exchangeName] || [];

        // Êó¢Â≠ò„ÅÆ„Éá„Éº„Çø„Å®Êñ∞„Åó„ÅÑ„É™„Ç¢„É´„Çø„Ç§„É†„Éá„Éº„Çø„ÇíÁµêÂêà
        const allData = [...dataset.data];
        realtimePoints.forEach(point => {
          // ÈáçË§á„ÇíÈÅø„Åë„Çã„Åü„ÇÅ„ÄÅÂêå„ÅòÊôÇÂàª„ÅÆ„Éá„Éº„Çø„ÅØÊõ¥Êñ∞
          const existingIndex = allData.findIndex(p => 
            Math.abs(p.x.getTime() - point.x.getTime()) < 5000 // 5Áßí‰ª•ÂÜÖ„Å™„ÇâÂêå„Åò„Å®„Åø„Å™„Åô
          );
          if (existingIndex >= 0) {
            allData[existingIndex] = point;
          } else {
            allData.push(point);
          }
        });

        // ÊôÇÂàªÈ†Ü„Å´„ÇΩ„Éº„Éà
        allData.sort((a, b) => a.x.getTime() - b.x.getTime());

        return {
          ...dataset,
          data: allData
        };
      });

      return { datasets: updatedDatasets };
    });
  };

  // WebSocket„Åß„É™„Ç¢„É´„Çø„Ç§„É†Êõ¥Êñ∞„ÇíÂèó‰ø°
  useEffect(() => {
    if (prices && prices.length > 0) {
      updateChartWithRealtime(prices);
    }
  }, [prices]);

  useEffect(() => {
    fetchPriceHistory();
    const interval = setInterval(fetchPriceHistory, 30000); // 30Áßí„Åî„Å®„Å´Êõ¥Êñ∞
    return () => clearInterval(interval);
  }, [timeRange]);

  const calculateTimeRange = () => {
    const now = new Date();
    const startTime = new Date(now.getTime() - timeRange * 60 * 60 * 1000);
    return { min: startTime, max: now };
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: `BTC/JPY ‰æ°Ê†ºÊé®Áßª (ÈÅéÂéª${timeRange}ÊôÇÈñì)`
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            return `${context.dataset.label}: ¬•${context.parsed.y.toLocaleString()}`;
          }
        }
      }
    },
    scales: {
      x: {
        type: 'time',
        time: {
          displayFormats: {
            hour: 'HH:mm',
            minute: 'HH:mm'
          }
        },
        title: {
          display: true,
          text: 'ÊôÇÂàª'
        },
        min: calculateTimeRange().min,
        max: calculateTimeRange().max
      },
      y: {
        title: {
          display: true,
          text: '‰æ°Ê†º (JPY)'
        },
        ticks: {
          callback: function(value) {
            return '¬•' + value.toLocaleString();
          }
        }
      }
    },
    interaction: {
      mode: 'nearest',
      axis: 'x',
      intersect: false
    }
  };

  return (
    <div className="price-chart">
      <div className="chart-controls">
        <div className="control-group">
          <label htmlFor="timeRange">Ë°®Á§∫ÊúüÈñì: </label>
          <select 
            id="timeRange"
            value={timeRange} 
            onChange={(e) => setTimeRange(parseInt(e.target.value))}
          >
            <option value={0.5}>30ÂàÜ</option>
            <option value={1}>1ÊôÇÈñì</option>
            <option value={2}>2ÊôÇÈñì</option>
            <option value={6}>6ÊôÇÈñì</option>
            <option value={12}>12ÊôÇÈñì</option>
            <option value={24}>24ÊôÇÈñì</option>
            <option value={72}>3Êó•Èñì</option>
            <option value={168}>1ÈÄ±Èñì</option>
          </select>
        </div>
        <div className="control-group">
          <a 
            href={`http://localhost:3001/api/export-csv?hours=${timeRange}`}
            download
            className="export-button"
          >
            üìä CSVÂá∫Âäõ
          </a>
        </div>
      </div>
      <div className="chart-container">
        <Line ref={chartRef} data={chartData} options={options} />
      </div>
    </div>
  );
};

export default PriceChart;
